function getApi(){fetch("https://picsum.photos/v2/list?page=1&limit=10").then(function(t){return t.json()}).then(function(t){var n="<ul>";t.forEach(function(t){n+=`<li><p>Author : ${t.author}</p><h3>Photograph Link : <a href="${t.url}">${t.url}</a></h3><br><h4>Photograph Download Link : <a href="${t.download_url}">${t.download_url}</a></h4></li>`}),n+="</ul>",document.getElementById("section-b").innerHTML+=n}),button1.style.visibility="hidden"}document.querySelector("#button1").addEventListener("click",getApi);